import { Navigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const AuthRedirectRoute = ({ children }) => {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return <div>Au...</div>;
  }

  if (user) {
    return <Navigate to="/" replace />;
  }
  return children;
};

export default AuthRedirectRoute;
